INSERT INTO `pc_知识列表` VALUES ('32', '1479286927068', '地基基础', '材料检验', 'A1-27', '土钉墙支护土钉 灌注浆体抗压强度（批量≤30根）', '每灌注30根土钉(或检验批)同配合比浆体，留置试件应不少于l组', '6块×70.7×70.7', '', '土钉墙支护是由设置于基坑边坡中的土钉、喷射混凝土面层及原状土体共同工作形成的支护结构，土钉墙常与截水帷幕、微型桩和预应力锚杆(索)等共同工作形成复合土钉墙支护结构；\r\n  土钉通常采取土中钻孔，置入变形钢筋、钢管并沿孔全长注浆的方法做成，对于没有类似经验的土钉墙工程，在正式施工前，应进行土打的抗拔力基本试验', '2016-11-16/17:4:40', null);
INSERT INTO `pc_知识列表` VALUES ('33', '1479287044863', '地基基础', '材料检验', 'A1-28', '喷射混凝土面层 混凝土抗压强度（批量≤500㎡）', '每喷射500 ㎡(或检验批)同配合比混凝土，留置试件应不少于l组', '3块×100×100×100(mm)组', '', '土钉墙支护是由设置于基坑边坡中的土钉、喷射混凝土面层及原状土体共同工作形成的支护结构，土钉墙常与截水帷幕、微型桩和预应力锚杆(索)等共同工作形成复合土钉墙支护结构；\r\n  土钉通常采取土中钻孔，置入变形钢筋、钢管并沿孔全长注浆的方法做成，对于没有类似经验的土钉墙工程，在正式施工前，应进行土打的抗拔力基本试验', '2016-11-16/17:6:1', null);
INSERT INTO `pc_知识列表` VALUES ('34', '1479287125680', '地基基础', '材料检验', 'A1-29', '排桩支护锚杆（索） 灌筑浆体抗压强度（批量≤30根）', '每灌筑30根锚杆(或检验批)同配合比浆体，留置试件应不少于1组', '6块×70.7×70.7×70.7(mm)/组', '', '排桩支护根据基坑深度、土的性质、基坑周边环境条件及施工场地条件等因素，采用锚杆—排桩、内支撑—排桩、悬臂式排桩或双排桩等结构形式；排桩根据土层的性质选择混凝土灌注桩、锚杆与土钉墙支护结构一起组成复合土钉墙支护结构；用于安全等级为一、二级的基坑或缺乏经验的地层中的锚杆，施工前应进行基本试脸，如果锚杆的锚固段主要位于钻土层、淤泥质土层、回填土层时，还应进行蠕变试验，且蠕变试验的锚杆数量不应少于3根', '2016-11-16/17:6:59', null);
