INSERT INTO `pc_知识列表` VALUES ('95', '1479377072828', '地基基础', '现场检测', 'B1-41', '截水帷幕 抽水试验（参见计算规则）', '板墙状帷幕:抽检数量不宜少于3个点', '现场观测\r\n(按设计要求)', '复验', '截水帷幕是为阻截或减小基坑侧壁及基坑底面下地下水流入而采用的连续截水体;截水体通常采用深层搅拌桩、高压喷射注装等;在无经验地区或\r\n对于重要工程，施工结束后，对桩式帷幕应进行压水试验.对板墙状帷幕压行抽水试验;基坑降水期间，应进行水位观测和沉降观测', '2016-11-17/18:5:31', null);
