INSERT INTO `pc_知识列表` VALUES ('40', '1479288049481', '地基基础', '材料检验', 'A1-35', '建筑场地土壤比活度 内照射指数或外照射指数（批量=1）', '在基础工程覆盖范围内，当场地土壤中氡浓度检测结果大于或等于50000Bq/m³时，抽检不少于1次', '当采用异地土作为室内回填土时，应对该取土的土壤，抽检不少于1次', '复验', '地基及填土污染物检测应符合下列规定:\r\n1)土壤氛浓度检测应在基坑、基槽开挖到设计高程后进行;\r\n2)建筑场地土壤比活度检测、建筑室内回填土比活度检测只适用于I类民用建筑工程', '2016-11-16/17:25:0', null);
