INSERT INTO `pc_知识列表` VALUES ('91', '1479376850357', '地基基础', '现场检测', 'B1-37', '地下连续墙支护结构 永久性结构承载力钻芯法检测（参见计算规则）', '当地下连续墙作为永久性结构，每个_L二程抽检15%，且不少于10个槽段，每个槽段不少于3个孔；', ' 现场检测\r\n(当钻芯法检测不满足设计要求时，应按不满足设计要求的槽段加倍扩大抽检)', '复验', '排桩支护根据基坑深度、土的性质、基坑周边环境条件及施工场地条件等因素，采用锚杆—排桩、内支撑—排桩、悬臂式排桩或双排桩等结构形式；排桩根据土层的性质选择混凝土灌注桩、锚杆与土钉墙支护结构一起组成复合土钉墙支护结构；用于安全等级为一、二级的基坑或缺乏经验的地层中的锚杆，施工前应进行基本试脸，如果锚杆的锚固段主要位于钻土层、淤泥质土层、回填土层时，还应进行蠕变试验，且蠕变试验的锚杆数量不应少于5根', '2016-11-17/18:2:10', null);
