INSERT INTO `pc_知识列表` VALUES ('97', '1479428695464', '地基基础', '现场检测', 'B1-43', '建筑场地回填土 压实系数（参见计算规则）', '每单位工程不应少于3点，每压实层(≤300mm)1000m³以上工程，每100㎡至少应有1点,3000㎡以上工程，每300㎡时至少应有1点', '现场检测', '复验', '', '2016-11-18/8:50:57', null);
