INSERT INTO `pc_知识列表` VALUES ('254', '1479950744690', '钢结构工程', '材料检验', 'A4-15', '防火涂料薄涂型 粘结强度、抗压强度（批量≤100t）', '同厂家、同品种、同批号，且≤100t的薄涂型产品或≤300 t的厚涂型产品，抽检不少于1次', ' 5kg/次\r\n(薄涂型的防火涂料仅检验粘结强度)', '复检', '防腐涂料的VOC和苯、甲苯、二甲苯、乙苯和防火涂料的游离甲醛等项目检验适用于民用建筑室内工程', '2016-11-24/9:28:34', null);
INSERT INTO `pc_知识列表` VALUES ('255', '1479950891115', '钢结构工程', '材料检验', 'A4-16', '防火涂料厚涂型 粘结强度、抗压强度（批量≤300t）', '同厂家、同品种、同批号，且≤100t的薄涂型产品或≤300 t的厚涂型产品，抽检不少于1次', '5kg/次\r\n(薄涂型的防火涂料仅检验粘结强度)', '复检', '防腐涂料的VOC和苯、甲苯、二甲苯、乙苯和防火涂料的游离甲醛等项目检验适用于民用建筑室内工程', '2016-11-24/9:30:2', null);
