INSERT INTO `pc_知识列表` VALUES ('271', '1479953538849', '建筑装饰装修工程', '材料检验', 'A5-1', '现场拌制抹灰砂浆 散装水泥常规性能（批量≤500t）', '同厂家、同品种、同强度等级、同批号，且≤500 t(散装水泥)或≤200 t（袋装水泥)的产品，抽检不少于1次', '12kg/次', '复验', '建筑装饰装修工程材料验收应符合下列规定:\r\n1)除用于现场修补或少量使用外，应限制使用现场拌制砂桨(包括抹灰、地面砂桨等);\r\n2)原材料的放射性性能指标、游离甲醛、挥发性有机化合物(VOC)、苯、甲苯、二甲苯、游离甲苯二异氰酸醋(TDI)等污染物检验项目，适用于民用、公用建筑室内工程', '2016-11-24/10:13:29', null);
