INSERT INTO `pc_知识列表` VALUES ('284', '1479954857741', '建筑装饰装修工程', '材料检验', 'A5-14', '抹灰砂浆施工质量 粘结强度抗拔试验（批量≤5000㎡）', '同厂家、同品种、同强度等级、问配合比.且≤5000㎡的外墙、顶棚抹灰砂浆及抹灰砂浆抗压强度检验不合格的内墙，抽检不少于1组；当外墙或顶棚抹灰砂浆抗压强度检验不合格时，抽检不少于2组', '现场检测(取样面积不少于2㎡，取样数量应为7个)', '复验', '建筑装饰装修工程材料验收应符合下列规定:\r\n1)除用于现场修补或少量使用外，应限制使用现场拌制砂桨(包括抹灰、地面砂桨等);\r\n2)原材料的放射性性能指标、游离甲醛、挥发性有机化合物(VOC)、苯、甲苯、二甲苯、游离甲苯二异氰酸醋(TDI)等污染物检验项目，适用于民用、公用建筑室内工程', '2016-11-24/10:35:41', null);
