INSERT INTO `pc_知识列表` VALUES ('289', '1479959674547', '建筑装饰装修工程', '材料检验', 'A5-19', '门窗玻璃 厚度、钢化玻璃的表面应力、碎片状态、抗冲击性、霞弹袋冲击性能等（批量=1）', '同厂家、同品种、同规格的产品，抽检不少于1次', '按相应检验标准规定取样', '复验', '下列情况门窗必须采用安全玻璃:\r\n1)在人员流动较多、可能产生拥挤和儿童集中的公共场所的门和落地窗，必须采用钢化玻璃或夹层玻璃等安全玻璃，并设置明显的警示标志；\r\n2)层数≥7层时，应采用安全玻璃；\r\n3)单块玻璃大于1.5㎡时，应采用安全玻璃；\r\n4)无室外阳台的外窗台多巨内地面高度小于0.9 m时，应采用安全玻璃', '2016-11-24/11:55:58', null);
