INSERT INTO `pc_知识列表` VALUES ('365', '1480383852633', '建筑装饰装修工程', '现场检测', 'B5-18', '疏散出口 踏步、门槛等（参见计算规则）', '全数检查', '  现场检查\r\n(疏散出口的门内门外1.40m范围内不应设踏步，且门必须向外开，并不应设置门槛)', '检验', '', '2016-11-29/9:45:42', null);
