INSERT INTO `pc_知识列表` VALUES ('401', '1480389984908', '屋面工程', '材料检验', 'A6-30', '水泥砂浆找平层 砂浆抗压强度（批量≤1000㎡）', '每检验批，且≤1000㎡，找平层积的同配合比水泥砂浆或细石混凝上，抽检不少于1组', '3块x70.7x70.7x70.7（mm）/组', '复验', '卷材、涂膜防水层要求基层有较好的结构整体性和刚度，目前大多数建筑均以钢筋泥凝上结构为主、故应采用水泥砂;、细石二凝土找平层作为防水基层;结构二凝土屋面板的找平层宜采用原桨抹平压光或加浆抹平压光；采用水泥竺浆找平时，宜未用1:3加抗裂纤维水泥砂装；保温层上找平层应采用不小于30mm厚的C20细石泥凝土', '2016-11-29/11:39:14', null);
