INSERT INTO `pc_知识列表` VALUES ('414', '1480392101799', '屋面工程', '现场检测', 'B6-10', '保温层 保温材料铺设（参见计算规则）', '按防水面积每100㎡检查1处，每处10㎡，且少于于3处', ' 现场检查\r\n(1.保温材料严禁使用吸水率高的材料，如膨胀珍珠岩、陶粒等;\r\n2.隔离层所用材料的质量应符设计要求，一般采用的材料有玻纤布、无纺布、塑料膜等)', '检验', '', '2016-11-29/12:3:33', null);
